// Simple "pop" sound as base64 to avoid external dependencies
const popSoundBase64 = 'data:audio/mp3;base64,';

export const playMessageSound = () => {
    try {
        const audio = new Audio(popSoundBase64);
        audio.volume = 0.5;
        audio.play().catch(e => console.log('Audio play failed (user interaction required):', e));
    } catch (error) {
        console.error('Error playing sound:', error);
    }
};
